#!/bin/bash

DIR="/dev"
DATA=""

check_and_create_file() {
    local file_name=$1
    if [ ! -f "$DIR/$file_name" ]; then
        /greenhouse/busybox touch "$DIR/$file_name"
        /greenhouse/busybox chmod 777 "$DIR/$file_name"
    fi
}

check_and_create_file2() {
    local file_name=$1
    if [ ! -f "$DIR/$file_name" ]; then
        /greenhouse/busybox touch "$DIR/$file_name"
        /greenhouse/busybox chmod 777 "$DIR/$file_name"
    fi

    if [ -c "$DIR/$file_name" ] || [ -b "$DIR/$file_name" ]; then
        /greenhouse/busybox rm "$DIR/$file_name"
        /greenhouse/busybox touch "$DIR/$file_name"
        /greenhouse/busybox chmod 777 "$DIR/$file_name"
    fi
}

check_and_write_if_empty() {
    local file_name=$1
    if [ -c "$file" ]; then # make sure "random" is not special dev 0327 update, blank state random is normal text file, only when it is char device, we delete and create it
        /greenhouse/busybox rm "$DIR/$file_name"
        /greenhouse/busybox echo "$DATA" >> "$DIR/$file_name"
    fi
}


